mapper.select:
select label
from django_user
where id_user = #{id_user}

select id_store
from django_recomm
where label = #{label}

select id_bookmark, id_store, id_user, active
from bookmark
where id_store=#{id_store} and id_user=#{id_user}

mapper.update:
update bookmark
set active = 1
where id_store = #{id_store} and id_user = #{id_user}

update bookmark
set active = 0
where id_store = #{id_store} and id_user = #{id_user}

update store
set hits = hits+1
where id_store = #{id_store}

mapper.select:
select store_name
from store
where id_store = #{id_store} and active = 1;

select avg(score)
from review
where id_store = #{id_store} and active = 1;

select address
from store
where id_store = #{id_store} and active = 1;
